mapper.update:

    		UPDATE roomInfo
			SET categoryNum = #{categoryNum},
			    roomKeyword = #{roomKeyword}
			WHERE roomNum = #{roomNum}
			AND roomMaker = #{userNum}


    		UPDATE roomInfo
			SET roomTitle = #{roomTitle},
			    roomDescription = #{roomInfo},
			    roomThumbNail = #{roomThumbNail}
			WHERE roomNum = #{roomNum}
			AND roomMaker = #{userNum}
